const ADD_POST = 'ADD_POST'

// 액션 객체, 동적이어야 한다면 동적 객체
export const addPost = {
    type: ADD_POST,
}

const dummyPost = {}

const reducer = (state = initialState, acton) => {
    switch (action.type) {
        case ADD_POST:
            return {
                ...state,
                mainPost: [dummyPost, ...state.mainPosts],
                postAdded: true,
            }

        default:
            return state
    }
}

export default reducer
