const initialState = {
    isLoggedIn: false,
    user: null,
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN_ACTION':
            return {
                ...state,
                isLoggedIn: true,
                user: action.data,
            }

        case 'LOGOUT_ACTION':
            return {
                ...state,
                isLoggedIn: false,
                user: null,
            }
        default:
            return state
    }
}

export default reducer
