import React from 'react';
import {Card} from 'antd';

// 컴포넌트 설계시 대충 한번 그려보는 거다있음
// 안드로이드와 비슷하다

// post 객체는 부모로부터 직접 받아오고 있다

const PostCard = ({post}) => {
    return <div>
        <Card
        cover ={post.Images[0] && <PostImages images={post.Images}/>}
        actions = {[
            <HeartOutlined/>

        ]}
        >
            <CardHead>
            </CardHead>

            <Image/>
            <Content/>
            <Like/>
        <Comment/>
        <CommentForm/>
        </Card>

    </div>
}

export default PostCard
