const initialState = {
    isLoggedIn: false,
    user: null,
}

export const loginAction = data => {
    return {
        type: 'LOGIN_ACTION',
        data,
    }
}
export const logoutAction = data => {
    return {
        type: 'LOGOUT_ACTION',
        data,
    }
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN_ACTION':
            return {
                ...state,
                isLoggedIn: true,
                user: action.data,
            }

        case 'LOGOUT_ACTION':
            return {
                ...state,
                isLoggedIn: false,
                user: null,
            }
        default:
            return state
    }
}

export default reducer
