import React,{ useState,useCallback} from 'react';
import {useSelector} from 'react-redux';
import {Card, Comment, Tooltip} from 'antd';
import {HeartOutlined, MessageOutlined, HeartTwoTone,LikeOutline,DislikeOutline, DislikeFilled, LikeFilled} from '@ant-design/icons';
import PostImages from './PostImages';
// 컴포넌트 설계시 대충 한번 그려보는 거다있음
// 안드로이드와 비슷하다

// post 객체는 부모로부터 직접 받아오고 있다
// 반복문을 통해 생성되는 key를 넣어줘야 함
const PostCard = ({post}) => {

    // const {me} = useSelector((state) => state.user);
    const [commentFormOpened, setCommentFormOpened] = useState(false);
    const [liked, setLiked] = useState(false);
    const onToggleLike = useCallback(()=> {

        setLiked((prev) => !prev);

    },[]);
    const onToggleComment = useCallback(()=> {

        setCommentFormOpened((prev) => !prev);

    },[]);
    // const id = me?.id;

    return <div>
        <Card
        // &&  expression -> true일때만 렌더링 된다.
        cover ={post.Images[0] && <PostImages images={post.Images}/>}
        actions = {[
            <HeartOutlined key="heart"/>,
            <MessageOutlined key="message" onClick={onToggleComment}/>,
            liked
                ? <HeartTwoTone towToneColor="#eb2f96" key="heart" onClick={onToggleLike}/>
                : <HeartOutlined key="heart" onClick ={onToggleLike}/>



        ]}
        >
            {/* <CardHead>
            </CardHead>

            <Image/>
            <Content/>
            <Like/>
        <Comment/>
        <CommentForm/> */}
        </Card>
        {commentFormOpened &&
        <div >
            댓글 부분 </div>}


    </div>
}

export default PostCard
